import AddCircleIcon from "@mui/icons-material/AddCircle";
import AutoFixHighIcon from "@mui/icons-material/AutoFixHigh";
import ClearAllIcon from "@mui/icons-material/ClearAll";
import DifferenceIcon from "@mui/icons-material/Difference";
import { Chip, Rating, Stack } from "@mui/material";
import Box from "@mui/material/Box";
import Checkbox from "@mui/material/Checkbox";
import IconButton from "@mui/material/IconButton";
import Paper from "@mui/material/Paper";
import { alpha } from "@mui/material/styles";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TablePagination from "@mui/material/TablePagination";
import TableRow from "@mui/material/TableRow";
import TableSortLabel from "@mui/material/TableSortLabel";
import Toolbar from "@mui/material/Toolbar";
import Tooltip from "@mui/material/Tooltip";
import Typography from "@mui/material/Typography";
import { visuallyHidden } from "@mui/utils";
import * as React from "react";
import { useState } from "react";
import { useEffect } from "react";
import "react-perfect-scrollbar/dist/css/styles.css";
import { useDispatch, useSelector } from "react-redux";
import { Link, NavLink } from "react-router-dom";
import PaginationBar from "../../../components/pagination/PaginationBar";
import SearchInput from "../../../components/searchInput/SearchInput";
import useAuth from "../../../hooks/useAuth";
import { fCurrency } from "../../../utils/numberFormat";
import { deleteProduct, getProductList, handleChangeFilters, handleClearFilters } from "../../product/productSlice";
import ProductSort from "../../product/ProductSort";


function descendingComparator(a, b, orderBy) {
  if (b[orderBy] < a[orderBy]) {
    return -1;
  }
  if (b[orderBy] > a[orderBy]) {
    return 1;
  }
  return 0;
}

function getComparator(order, orderBy) {
  return order === "desc"
    ? (a, b) => descendingComparator(a, b, orderBy)
    : (a, b) => -descendingComparator(a, b, orderBy);
}


function stableSort(array, comparator) {
  const stabilizedThis = array?.map((el, index) => [el, index]);
  stabilizedThis.sort((a, b) => {
    const order = comparator(a[0], b[0]);
    if (order !== 0) {
      return order;
    }
    return a[1] - b[1];
  });

  return stabilizedThis.map((el) => el[0]);
}

const headCells = [
  {
    id: "_id",
    aligns: "left",
    disablePadding: true,
    label: "Product",
  },
  {
    id: "types",
    aligns: "left",
    disablePadding: false,
    label: "Types",
  },
  {
    id: "status",
    aligns: "center",
    disablePadding: false,
    label: "Status",
  },
  {
    id: "price",
    aligns: "right",
    disablePadding: false,
    label: "Price",
  },
   
  {
    id: "Unit",
    aligns: "right",
    disablePadding: false,
    label: "Unit",
  },

];

function EnhancedTableHead(props) {
  const {
    onSelectAllClick,
    order,
    orderBy,
    numSelected,
    rowCount,
    onRequestSort,
  } = props;
  const createSortHandler = (property) => (event) => {
    onRequestSort(event, property);
  };

  return (
    <TableHead>
      <TableRow>
        <TableCell padding="checkbox">
          <Checkbox
            color="primary"
            indeterminate={numSelected > 0 && numSelected < rowCount}
            checked={rowCount > 0 && numSelected === rowCount}
            onChange={onSelectAllClick}
            inputProps={{
              "aria-label": "select all desserts",
            }}
          />
        </TableCell>
        {headCells.map((headCell) => (
          <TableCell
            key={headCell.id}
            align={headCell.aligns}
            padding={headCell.disablePadding ? "none" : "normal"}
            sortDirection={orderBy === headCell.id ? order : false}
          >
            <TableSortLabel
              active={orderBy === headCell.id}
              direction={orderBy === headCell.id ? order : "asc"}
              onClick={createSortHandler(headCell.id)}
            >
              {headCell.label}
              {orderBy === headCell.id ? (
                <Box component="span" sx={visuallyHidden}>
                  {order === "desc" ? "sorted descending" : "sorted ascending"}
                </Box>
              ) : null}
            </TableSortLabel>
          </TableCell>
        ))}
      </TableRow>
    </TableHead>
  );
}

const EnhancedTableToolbar = (props) => {
  const dispatch = useDispatch();
  const { numSelected, selected, setSelected, setPage, setProductCurrent, setOpen, user, product } = props;
  const handleSubmit = (searchQuery) =>
    dispatch(handleChangeFilters({ productName: searchQuery }));
  const handleDispatch = (value) =>
    dispatch(handleChangeFilters(value));
     const handleDeleteProduct = (id) => {
    if (user._id) dispatch(deleteProduct(id));
  };

  const handleClickOpen = (product) => {
    setProductCurrent(product);
    setOpen(true);
  };

  return (
    <Toolbar
      sx={{
        pl: { sm: 2 },
        pr: { xs: 1, sm: 1 },
        ...(numSelected > 0 && {
          bgcolor: (theme) =>
            alpha(
              theme.palette.primary.main,
              theme.palette.action.activatedOpacity
            ),
        }),
      }}
    >
      {numSelected > 0 ? (
        <Typography
          sx={{ flex: "1 1 100%" }}
          color="inherit"
          variant="subtitle1"
          component="div"
        >
          {numSelected} selected
        </Typography>
      ) : (
        <Stack
          direction="row"
          alignItems="center"
          sx={{ flex: "1 1 100%" }}
          spacing={2}
        >
          <SearchInput handleSubmit={handleSubmit} />
          <ProductSort handleDispatch={handleDispatch} />
        </Stack>
      )}
      {numSelected > 0 ? (
        <Tooltip title="Clone Product">
          <IconButton
            component={NavLink}
            to={`/dashboard/products/clone/${selected[0]}`}
            onClick={() => setSelected([])}
          >
            <DifferenceIcon />
          </IconButton>
        </Tooltip>
      ) : (
        <Tooltip title="Add Product">
          <IconButton
            component={NavLink}
            to={`/dashboard/products/add`}
            onClick={() => setSelected([])}
          >
            <AddCircleIcon />
          </IconButton>
        </Tooltip>
      )}

      {numSelected > 0 ? (
        <Tooltip title="Edit Product">
          <IconButton
            component={NavLink}
            // to={`/dashboard/products/edit/${selected[0]}`}
            onClick={() => handleClickOpen(product)}
          >
            <AutoFixHighIcon />
          </IconButton>
        </Tooltip>
      ) : (
        <Tooltip title="Clear filter">
          <IconButton
            onClick={() => {
              setPage(0);
              dispatch(handleClearFilters());
            }}
          >
            <ClearAllIcon />
          </IconButton>
        </Tooltip>
      )}
    </Toolbar>
  );
};

export default function ProductTable() {
  const { products, totalProduct, filters, totalPages, error, isLoading } = useSelector(
    (state) => state.product
  );
  const dispatch = useDispatch();
 const  [open, setOpen] = useState(false);
  const [productCurrent, setProductCurrent] = useState([]);
const {user} = useAuth()
  const [order, setOrder] = useState("asc");
  const [orderBy, setOrderBy] = useState("price");
  const [selected, setSelected] = useState([]);
  const [page, setPage] = useState(1);

  const [rowsPerPage, setRowsPerPage] = React.useState(10);

   
  const handleRequestSort = (event, property) => {
    const isAsc = orderBy === property && order === "asc";
    setOrder(isAsc ? "desc" : "asc");
    setOrderBy(property);
  };

  const handleSelectAllClick = (event) => {
    if (event.target.checked) {
      const newSelecteds = products.map((n) => n._id);
      setSelected(newSelecteds);

      return;
    }
    setSelected([]);
  };

  const handleClick = (event, _id) => {
    const selectedIndex = selected.indexOf(_id);
    let newSelected = [];

    if (selectedIndex === -1) {
      newSelected = newSelected.concat(selected, _id);
    } else if (selectedIndex === 0) {
      newSelected = newSelected.concat(selected.slice(1));
    } else if (selectedIndex === selected.length - 1) {
      newSelected = newSelected.concat(selected.slice(0, -1));
    } else if (selectedIndex > 0) {
      newSelected = newSelected.concat(
        selected.slice(0, selectedIndex),
        selected.slice(selectedIndex + 1)
      );
    }

    setSelected(newSelected);
  };


  const isSelected = (title) => selected.indexOf(title) !== -1;

  useEffect(() => {
    const filters = {page:page, limit:rowsPerPage}
    dispatch(getProductList(filters));
  }, [page, filters,rowsPerPage, dispatch]);

  return (
    <Box sx={{ width: "100%" }}>
      <Paper sx={{ width: "100%", mb: 2 }}>
      
        <EnhancedTableToolbar
          numSelected={selected.length}
          selected={selected}
          setSelected={setSelected}
          setPage={setPage}
          setProductCurrent={setProductCurrent}
          setOpen={setOpen}
          user={user}
          product={product}
        />
      
       
        <TableContainer>
          <Table
            sx={{ minWidth: 750 }}
            aria-labelledby="tableTitle"
            size={"medium"}
          >
            <EnhancedTableHead
              numSelected={selected.length}
              order={order}
              orderBy={orderBy}
              onSelectAllClick={handleSelectAllClick}
              onRequestSort={handleRequestSort}
              rowCount={products?.length}
            />
            <TableBody>
              {stableSort(products, getComparator(order, orderBy)).map(
                (row, index) => {
                  const isItemSelected = isSelected(row._id);
                  const labelId = `enhanced-table-checkbox-${index}`;

                  return (
                    <TableRow
                      hover
                      onClick={(event) => {
                        handleClick(event, row._id);
                      }}
                      role="checkbox"
                      aria-checked={isItemSelected}
                      tabIndex={-1}
                      key={row._id}
                      selected={isItemSelected}
                    >
                      <TableCell padding="checkbox">
                        <Checkbox
                          color="primary"
                          checked={isItemSelected}
                          inputProps={{
                            "aria-labelledby": labelId,
                          }}
                        />
                      </TableCell>
                      <TableCell
                        component="th"
                        id={labelId}
                        scope="row"
                        sx={{ p: 1 }}
                        width="500px"
                      >
                        <Box
                          sx={{
                            display: "flex",
                            alignItems: "center",
                            minWidth: "400px",
                          }}
                        >
                          <Box
                            sx={{
                              width: "100px",
                              height: "100px",
                              display: "inline-block",
                              mr: 2,
                            }}
                          >
                            <img
                              src={row?.image?.[0]}
                              alt={row?.productName}
                              style={{ width: "100%", height: "100%" }}
                            />
                          </Box>
                          <Typography
                            component={Link}
                            // to={`/detail/${row?._id}`}
                            variant="subtitle2"
                            color="initial"
                            sx={{ display: "inline-block", width: "70%" }}
                          >
                            {row?.productName}
                          </Typography>
                        </Box>
                      </TableCell>
                      <TableCell align="left">
                        <Typography variant="subtitle1" color="initial">
                          {row?.types}
                        </Typography>
                      </TableCell>
                      <TableCell align="right">
                        <Stack direction="row" alignItems="center">
                         
                          <Typography
                            variant="subtitle2"
                            noWrap
                            color="text.disabled"
                          >
                            ({row?.status})
                          </Typography>
                        </Stack>
                      </TableCell>
                      <TableCell align="right">
                        <Typography
                          variant="subtitle1"
                          color="initial"
                          sx={{
                            color: "text.disabled",
                            textDecoration: "line-through",
                          }}
                        >
                          {fCurrency(row?.price)}
                        </Typography>
                        <Typography variant="subtitle1" color="initial">
                          {fCurrency(row?.priceSale)}
                        </Typography>
                      </TableCell>
                      <TableCell align="right">
                        <Stack
                          direction="row"
                          alignItems="center"
                          justifyContent="flex-end"
                          spacing={1}
                        >
                          <Typography variant="subtitle1" color="initial">
                            {row?.unit}
                          </Typography>
                        </Stack>
                      </TableCell>
                    </TableRow>
                  );
                }
              )}
             
            </TableBody>
          </Table>
        </TableContainer>
         <Box
                    sx={{
                      mt: { xs: 2, md: 5 },
                      mb: { xs: 2, md: 5 },
                      display: "flex",
                      justifyContent: "center",
                    }}
                  >
                    {totalProduct ? (
                      <PaginationBar
                        page={page}
                        setPage={setPage}
                        totalPage={+totalPages}
                      />
                    ) : (
                      <Typography variant="h6">No Products Yet</Typography>
                    )}
                  </Box>
       
      </Paper>
    </Box>
  );
}
