import React, { useEffect, useState } from "react";
import {
  Alert,
  Box,
  Card,
  Container,
  IconButton,
  Pagination,
  Stack,
  TablePagination,
  Typography,
} from "@mui/material";
import { useDispatch, useSelector } from "react-redux";
import ProductTable from "./ProductTable";
import { getProducts } from "../../product/productSlice";

import PaginationBar from "../../../components/pagination/PaginationBar";
import ProductSearch from "../../product/ProductSearch";
import LoadingScreen from "../../../components/loadingScreen/LoadingScreen";
import "/src/pages/DashBoard/dash.css";
function ProductDashBoard() {
  let [filterName, setFilterName] = useState("");
  let [page, setPage] = useState(1);
  const dispatch = useDispatch();
  const {
    products,
    isLoading,
    error,
    totalProduct,
    totalPages,
  } = useSelector((state) => state.product);

  useEffect(
    (productName) => {
      productName = filterName;
      dispatch(getProducts({ page, productName }));
    },
    [page, dispatch,  filterName]
  );

  const handleSubmit = (searchQuery) => {
    setFilterName(searchQuery);
    setPage(1);
  };


  return (
    <Container>
      <Card sx={{ p: 3 }}>
        <Stack spacing={2}>
          <Stack direction="column" alignItems="center">
            <ProductSearch handleSubmit={handleSubmit} />

            <Typography
              variant="subtitle"
              sx={{ color: "text.secondary", mt: 1 }}
            >
              {totalProduct > 1
                ? `${totalProduct} products `
                : totalProduct === 1
                ? `${totalProduct} products `
                : "No products found"}
            </Typography>

            <Box sx={{ flexGrow: 1 }} />
           
          </Stack>
           <Box sx={{ position: "relative", height: 1 }}>
          {isLoading ? (
            <LoadingScreen />
          ) : (
            <>
              {error ? (
                <Alert severity="error">{error}</Alert>
              ) : (
                <Box className="container_dash">
                <ProductTable products={products} />
                </Box>
              )}
            </>
          )}
        </Box>
         
        </Stack>
        <Box
          sx={{
            mt: { xs: 2, md: 5 },
            mb: { xs: 2, md: 5 },
            display: "flex",
            justifyContent: "center",
          }}
        >
          {totalProduct ? (
            <PaginationBar
              page={page}
              setPage={setPage}
              totalPage={+totalPages}
            />
          ) : (
            <Typography variant="h6">No Products Yet</Typography>
          )}
        </Box>
      </Card>
    </Container>
  );
}

export default ProductDashBoard;
