import { CssBaseline } from "@mui/material";
import { createTheme, ThemeProvider } from "@mui/material/styles";

const PRIMARY = {

  lighter: "#99AAFF",
  light: "#4D4DFF",
  main: "#3355FF",
  dark: "#002AFF",
  darker: "#001999",
  contrastText: "#FFF",
};
const SECONDARY = {
  lighter: "#80deea",
  light: "#00acc1",
  main: "#0097a7",
  dark: "#00838f",
  darker: "#006064",
  contrastText: "#FFF",
};
const SUCCESS = {
  lighter: "#a5d6a7",
  light: "#43a047",
  main: "#388e3c",
  dark: "#2e7d32",
  darker: "#1b5e20",
  contrastText: "#FFF",
};

function MThemeProvider({ children }) {
  const themeOptions = {
    palette: {
      primary: PRIMARY,
      secondary: SECONDARY,
      success: SUCCESS,
    },
  };

  const theme = createTheme(themeOptions);

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      {children}
    </ThemeProvider>
  );
}

export default MThemeProvider;
