import { CssBaseline } from "@mui/material";
import {
    createTheme,
    ThemeProvider,
} from "@mui/material/styles";

const PRIMARY = {
 
     lighter: "#80deea",
    light: "#00acc1",
    main: "#0097a7",
    dark: "#00838f",
    darker: "#006064",
    contrastText: "#FFF",
};
const SECONDARY = {
      lighter: "#a5d6a7",
    light: "#43a047",
    main: "#388e3c",
    dark: "#2e7d32",
    darker: "#1b5e20",
    contrastText: "#FFF",
};
const SUCCESS = {
    lighter: "#f48fb1",
    light: "#f06292",
    main: "#d81b60",
    dark: "#c2185b",
    darker: "#ad1457",
    contrastText: "#FFF",
};


function MThemeProvider({ children }) {
    const themeOptions = {
        palette: {
            primary: PRIMARY,
            secondary: SECONDARY,
            success: SUCCESS
           
        }
    };

    const theme = createTheme(themeOptions);

    return (
        <ThemeProvider theme={theme}>
            <CssBaseline />
            {children}
        </ThemeProvider>
    );
}

export default MThemeProvider;
