import React, { useState, useRef, useEffect } from "react";
import apiService from "../../app/apiService";
import { slider } from "../../data/Data";
import "./sliderShow.css";

function SliderShow() {
  const [index, setIndex] = useState(0);
  const timeoutRef = useRef(null);

  // let sliders = [];

  // banner.forEach((slider) => {
  //     sliders = [...sliders, slider.sliderShow]
  //     console.log(slider.sliderShow)
  // });
  const [slider, setSlider] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

    useEffect(() => {
    const getSingleProducts = async () => {
      setLoading(true);
      try {
        const res = await apiService.get(`/sliders`);

        setSlider(res.data);
        setError("");
      } catch (error) {
        console.log(error);
        setError(error.message);
      }
      setLoading(false);
    };
    getSingleProducts();
     // eslint-disable-next-line
  }, []);

  function resetTimeout() {
    if (timeoutRef.current) {
      clearTimeout(timeoutRef.current);
    }
  }

  useEffect(() => {
    resetTimeout();
    timeoutRef.current = setTimeout(
      () =>
        setIndex((prevIndex) =>
          prevIndex === slider.length - 1 ? 0 : prevIndex + 1
        ),
      6000
    );
    return () => {
      resetTimeout();
    };
    // eslint-disable-next-line 
  }, [slider, index]);

  return (
    <div className="slideshow">
      <div
        className="slideshowSlider"
        style={{ transform: `translate3d(${-index * 100}%, 0, 0)` }}
      >
        {slider.map((item, index) => (
          <img key={item.id} className="slide" src={item.image} alt="" />
        ))}
      </div>

      <div className="slideshowDots">
        {slider.map((item, idx) => (
          <div
            key={item.id}
            className={`slideshowDot${index === idx ? " active" : ""}`}
            onClick={() => {
              setIndex(idx);
            }}
          ></div>
        ))}
      </div>
    </div>
    
  );
}

export default SliderShow;
