import React, { useEffect, useState } from "react";
import {
    Box,
    Card,
    Container,
    Pagination,
    Stack,
    Typography,

} from "@mui/material";

import useAuth from "../../hooks/useAuth";
import { useDispatch, useSelector } from "react-redux";
import SearchInput from "../../components/searchInput/SearchInput";
import ProductTable from "./ProductTable";
import { getProducts } from "../../product/productSlice";



function ProductDashBoard() {
    let [filterName, setFilterName] = useState("");
    let [page, setPage] = useState(1);

    const { user } = useAuth();
    const dispatch = useDispatch();
    const { products, count } = useSelector((state) => state.product)

    useEffect((name) => {
        name = filterName
        dispatch(getProducts({ id: user._id, page, name }))
    }, [page, dispatch, user._id, filterName])


    const handleSubmit = (searchQuery) => {
        setFilterName(searchQuery);
    };
    const handleChange = (even, value) => {
        setPage(value);
    };

    return (
        <Container>
            <Typography variant="h5" sx={{ fontSize: { xs: 16, md: 28 }, fontWeight: 600, mb: 2 }}>
                My Product
            </Typography>
            <Card sx={{ p: 3 }}>
                <Stack spacing={2}>
                    <Stack direction={{ xs: "column", md: "row" }} alignItems="center">
                        <SearchInput handleSubmit={handleSubmit} />

                        <Typography
                            variant="subtitle"
                            sx={{ color: "text.secondary", ml: 1 }}
                        >
                            {count > 1
                                ? `${count} products found`
                                : count === 1
                                    ? `${count} products found`
                                    : "No products found"}
                        </Typography>

                        <Box sx={{ flexGrow: 1 }} />

                        <Pagination
                            count={Math.ceil(count / 10)}
                            page={page} onChange={handleChange} />
                    </Stack>
                    <ProductTable products={products} />
                </Stack>
            </Card>
        </Container>
    );
}

export default ProductDashBoard;
