import { Button } from "@mui/material";
import { Stack } from "@mui/system";
import React from "react";
import styled from "styled-components";
import { banner } from "../../data/Data";
import "./Banner.css";
import ArrowLeftIcon from "@mui/icons-material/ArrowLeft";
import ArrowRightIcon from "@mui/icons-material/ArrowRight";
function Banner() {
  return (
    <>
      <BannerContainer>
        <h1>Banner</h1>
        <BannerSlider>
          {banner.map((items, index) => (
            <Stack key={items.id} className="bannerItems">
              <img key={items.id} src={items.cover} alt="" />
              <Button>By now</Button>
            </Stack>
          ))}
        </BannerSlider>
        <ArrowLeftIcon className="btnLeft" />
        <ArrowRightIcon className="btnRight" />
      </BannerContainer>
    </>
  );
}
export default Banner;

const BannerContainer = styled.div`
  background-color: var(--color-background);
  color: var(--color-white);
  padding: 20px 20px 0;
  position: relative;
  width: 100%;
  hight: 100%;

  .btnLeft {
    position: absolute;
    top: 50%;
    left: 30px;
    z-index: 20;
    transform-origin: center;
    cursor: pointer;
    background-color: rgba(0, 0, 0, 0.5);
    height: 80px;
    width: 35px;
    color: #fff;
    border-radius: 4px;
    display: flex;
    align-items: center;
    transform: translateY(-20%);
    &:hover {
      background-color: rgba(0, 0, 0, 0.8);
    }
    &:hover svg {
      opacity: 1;
      transform: scale(1.2);
    }

    svg {
      opacity: 0.7;
      font-size: 50px;
      transition: all 0.3s linear;
    }
  }
  .btnRight {
    position: absolute;
    top: 50%;
    right: 30px;
    z-index: 20;
    transform-origin: center;
    cursor: pointer;
    background-color: rgba(0, 0, 0, 0.5);
    height: 100px;
    width: 50px;
    color: #fff;
    border-radius: 4px;
    display: flex;
    align-items: center;
    transform: translateY(-20%);
    &:hover {
      background-color: rgba(0, 0, 0, 0.8);
    }
    &:hover svg {
      opacity: 1;
      transform: scale(1.2);
    }

    svg {
      opacity: 0.7;
      font-size: 50px;
      transition: all 0.3s linear;
    }
  }
`;
const BannerSlider = styled.div`
  display: grid;
  gap: 6px;
  grid-template-columns: repeat(${banner.length}, 300px);
  transition: all0.3s linear;
  user-select: none;
  overflow-y: hidden;
  overflow-x: auto;
  overflow: hidden;
  padding-top: 28px;
  padding-bottom: 28px;
  scroll-behavior: smooth;

  &:hover .bannerItems {
    opacity: 0.5;
  }

  .bannerItems {
    transform: scale(1);
    max-width: 400px;
    max-height: 500px;
    width: 100%;
    height: 100%;
    transition: all 0.3s linear;
    user-select: none;
    overflow: hidden;
    border-radius: 6px;
    transform: center left;
    positon: relative;

    &:hover {
      opacity: 1;
      transform: scale(1.1);
      z-index: 10;
    }

    img {
      width: 100%;
      height: 100%;
      object-fit: cover;
    }
  }
`;
