import React, { useEffect, useState } from "react";
import {
  Box,
  Card,
  Container,
  Pagination,
  Stack,
  TablePagination,
  Typography,
} from "@mui/material";

import { useDispatch, useSelector } from "react-redux";
import ProductTable from "./ProductTable";
import { getProducts } from "../../product/productSlice";
import useAuth from "../../../hooks/useAuth";
import PaginationBar from "../../../components/pagination/PaginationBar";
import ProductSearch from "../../product/ProductSearch";

function ProductDashBoard() {
  let [filterName, setFilterName] = useState("");
  let [page, setPage] = useState(1);
  const dispatch = useDispatch();
  const [rowsPerPage, setRowsPerPage] = React.useState(5);

  const {
    products,
    isLoading,
    totalProduct,
    totalPages,
  } = useSelector((state) => state.product);

  useEffect(
    (productName) => {
      productName = filterName;
      dispatch(getProducts({ page, productName }));
    },
    [page, dispatch,  filterName]
  );

  const handleSubmit = (searchQuery) => {
    setFilterName(searchQuery);
    setPage(1);
  };
  const handleChange = (even, value) => {
    setPage(value);
  };
    const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };

  return (
    <Container>
      
      <Card sx={{ p: 3 }}>
        <Stack spacing={2}>
          <Stack direction="column" alignItems="center">
            <ProductSearch handleSubmit={handleSubmit} />

            <Typography
              variant="subtitle"
              sx={{ color: "text.secondary", ml: 1 }}
            >
              {totalProduct > 1
                ? `${totalProduct} products found`
                : totalProduct === 1
                ? `${totalProduct} products found`
                : "No products found"}
            </Typography>

            <Box sx={{ flexGrow: 1 }} />
          </Stack>
          <ProductTable products={products} />
        </Stack>
        <Box
          sx={{
            mt: { xs: 2, md: 5 },
            mb: { xs: 2, md: 5 },
            display: "flex",
            justifyContent: "center",
          }}
        >
          {totalProduct ? (
            <PaginationBar
              page={page}
              setPage={setPage}
              totalPage={+totalPages}
            />
          ) : (
            <Typography variant="h6">No Products Yet</Typography>
          )}
        </Box>
      </Card>
    </Container>
  );
}

export default ProductDashBoard;
