import {
  Box,
  Card,
  Container,
  Divider,
  Grid,
  IconButton,
 
  Stack,
 
  Typography,
} from "@mui/material";
import LocalShippingIcon from '@mui/icons-material/LocalShipping';
import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import SearchFilter from "../../components/searchSelect/SearchFilter";
import OrderUpdateStatus from "../../features/dashboard/order/OrderUpdateStatus";
import { getOrdersDashboard } from "../../features/order/orderSlice";
import useAuth from "../../hooks/useAuth";
import MoreHorizIcon from "@mui/icons-material/MoreHoriz";

import { fCurrency } from "../../utils/numberFormat";
import ButtonStatus from "../../features/dashboard/order/ButtonStatus";
import PaginationBar from "../../components/pagination/PaginationBar";
import { TitleStyle } from "../../theme/customizations";
import "./dash.css";

function EditOrderPage() {
  const [open, setOpen] = useState(false);
  const [orders, setOders] = useState([]);
  let [page, setPage] = useState(1);
  let [filterName, setFilterName] = useState("");
  const { user } = useAuth();
  const userId = user._id;
  const dispatch = useDispatch();
  const { ordersDashboard, totalPageDashboard } = useSelector((state) => state.order);


  useEffect(
    (status) => {
      status = filterName;
      if (user) dispatch(getOrdersDashboard({ page, userId, status }));
    },
    [dispatch, page, user, userId, filterName]
  );

  const handleClickOpen = (order) => {
    setOders(order);
    setOpen(true);
  };
  const handleSubmit = (searchQuery) => {
    setFilterName(searchQuery);
  };
 

  return (
    <Container >
      <Stack direction="row" alignItems="center" sx={{ py: 2 }}>
        <TitleStyle>
          <LocalShippingIcon sx={{ width: "35px", height: "35px" }} />
          <Typography variant="h6" textAlign="left" sx={{ pl: 1 }}>
            Order
          </Typography>
        </TitleStyle>
      </Stack>
      <OrderUpdateStatus orders={orders} setOpen={setOpen} open={open} />

      <Card>
        <Box sx={{ mt: 2, display: "flex", justifyContent: "space-around" }}>
          <SearchFilter handleSubmit={handleSubmit} />

        </Box>

        <Container className="container_dash" sx={{ mb: 5 }}>
          <Grid container spacing={2} mt={1} mb={1}>
            {ordersDashboard?.map((order) => (
              <Grid key={order?._id} item xs={12} md={4} lg={3}>
                <Card sx={{ p: 3, mt: 1, mr: 2, minHeight: 500 }}>
                  <Box
                    sx={{
                      mb: 2,
                      display: "flex",
                      justifyContent: "space-between",
                    }}
                  >
                    <ButtonStatus status={order?.status} />
                    <IconButton
                      onClick={() => {
                        handleClickOpen(order);
                      }}
                      sx={{ height: 30, width: 30 }}
                    >
                      <MoreHorizIcon />
                    </IconButton>
                  </Box>
                  <Typography sx={{ mb: 2, fontSize: 13, fontWeight: 600 }}>
                    CodeID: {order?.orderId}
                  </Typography>

                  <Typography sx={{ mb: 2, fontSize: 13, fontWeight: 600 }}>
                    Buyer Name: {order?.buyerName}
                  </Typography>

                  <Typography sx={{ mb: 2, fontSize: 13, fontWeight: 600 }}>
                    Address Shiping: {order?.addressShiping}
                  </Typography>

                  <Typography sx={{ mb: 2, fontSize: 13, fontWeight: 600 }}>
                    Phone: {order?.phone}
                  </Typography>

                  <Divider sx={{ borderStyle: "dashed", mb: 2 }} />

                  <Typography sx={{ fontSize: 13, mb: 2, fontWeight: 600 }}>
                    Product: {order?.nameProduct}
                  </Typography>

                  <Typography
                    sx={{
                      fontSize: 13,
                      mb: 1,
                      fontWeight: 600,
                      display: "flex",
                      justifyContent: "space-between",
                      flexDirection: "row",
                    }}
                  >
                    <span>Price:</span>
                    <span style={{ color: "red" }}>
                      {fCurrency(order.price)}{" "}
                    </span>
                  </Typography>

                  <Typography
                    sx={{
                      fontSize: 13,
                      mb: 1,
                      fontWeight: 600,
                      display: "flex",
                      justifyContent: "space-between",
                      flexDirection: "row",
                    }}
                  >
                    <span>Amount:</span>
                    <span style={{ color: "red" }}>{order?.amount}</span>
                  </Typography>

                  <Typography
                    sx={{
                      fontSize: 13,
                      mb: 1,
                      fontWeight: 600,
                      display: "flex",
                      justifyContent: "space-between",
                      flexDirection: "row",
                    }}
                  >
                    <span>Sum:</span>
                    <span style={{ color: "red" }}>
                      {fCurrency(order?.sum)}{" "}
                    </span>
                  </Typography>

                  <Divider sx={{ borderStyle: "dashed", mb: 2 }} />

                  <Typography
                    sx={{ fontStyle: "italic", fontSize: 12, fontWeight: 300 }}
                  >
                    (Pay the money when receiving the goods)
                  </Typography>
                </Card>
              </Grid>
            ))}
          </Grid>
        </Container>
        <Box sx={{ display:"flex", justifyContent:"center"}}>
          <PaginationBar
           page={page} 
           setPage={setPage} 
           totalPage={+totalPageDashboard}
          />
        </Box>
      </Card>
      <Box sx={{ flexGrow: 1 }} />
      <Box sx={{ mb: 15 }} />
    </Container>
  );
}

export default EditOrderPage;
