import { Button } from "@mui/material";
import { Box } from "@mui/system";
import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import ProductList from "./ProductList";
import { getProductsTopSelling } from "./productSlice";
import { Grid } from "@mui/material";
import ProductCard from './ProductCard';


function ProductTopList() {
  const {
    currentPageProducts,
    productById,
    isLoading,
    totalProduct,
    productsTopSelling,
  } = useSelector((state) => state.product);
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(getProductsTopSelling({}));
  }, [dispatch]);

  return (
    <div>
      <Box
        sx={{
          mt: 5,
          mb: 5,
          textAlign: "center",
          display: "flex",
          justifyContent: "center",
          flexDirection: "column",
        }}
      >
        <Button
          sx={{
            fontSize: { xs: 18, md: 25, lg: 35 },
            m: 2,
            fontFamily: "Comic Sans MS",
            color: "#000",
          }}
        >
          Top Selling
        </Button>
       <Grid container spacing={1} mt={0}>
            {productsTopSelling.map((item, index) => (
              <Grid key={item.id} item xs={8} sm={6} md={4} lg={3}>
                <ProductCard product={item} />
              </Grid>
            ))}
     </Grid>
  
      </Box>
    </div>
  );
}

export default ProductTopList;
