import { Stack } from '@mui/system';
import React from 'react'
import styled from 'styled-components';
import { banner } from '../../data/Data';
import "./Banner.css";

function Banner() {
   
  return (
    <>
    <BannerContainer>
    <h1>Banner</h1>
    <BannerSlider>
    {banner.map((items, index)=> (
        <Stack key={items.id} className="bannerItems">
            <img
            key={items.id}
            src={items.cover}
            alt=""
            />
        </Stack>
    ))}
    </BannerSlider>
    </BannerContainer>
    </>
  )
  }
export default Banner;

const BannerContainer = styled.div`
background-color: var(--color-background);
color: var(--color-white);
padding: 20px 20px 0;
position: relative;
width:100%;
hight:100%;

`;
const BannerSlider = styled.div`

display: grid;
gap: 6px;
transition: all0.3s linear;
user-select: none;
overflow-y: hidden;
overflow-x: auto;
overflow: hidden;
padding-top: 28px;
padding-bottom:28px;
scroll-behavior: smooth;
.bannerItems {
    transform: scale(1);
    max-width:400px;
    max-height:500px;
    width:100%;
    height:100%;
    transition: all 0.3s linear;
    user-select:none;
    overflow:hidden;
    border-radius: 6px;
    transform: center left;
    positon: relative;

    &:hover {
        transform: scale(1.1);
        z-index:10;
    }
}



`;
