import { Alert, Button, Grid, Typography } from "@mui/material";
import { Box, Container, Stack } from "@mui/system";
import { LoadingButton } from "@mui/lab";
import React, { useEffect, useState } from "react";
import { useForm } from "react-hook-form";
import apiService from "../app/apiService";
import FormProvider from "../components/form/FormProvider";
import LoadingScreen from "../components/loadingScreen/LoadingScreen";
import ProductFilter from "../features/product/ProductFilter";
import ProductList from "../features/product/ProductList";
import ProductSearch from "../features/product/ProductSearch";
import ProductSort from "../features/product/ProductSort";
import "./stylePage.css";
import { getProducts, handleChangeFilters } from "../features/product/productSlice";

import { useDispatch, useSelector } from "react-redux";
import PaginationBar from "../components/pagination/PaginationBar";
import { PER_PAGE } from "../app/config";
import { applyFilter } from "../hooks/useApply";

function StorePage() {
  const dispatch = useDispatch();
  const [productFilter, setProductFilter ] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [filterName, setFilterName] = useState("");
  const [page, setPage] = useState(1);

  const { currentPageProducts, productById, isLoading, totalProduct, totalPages } =
    useSelector((state) => state.product);

  let products = currentPageProducts.map((product) => productById[product]);
  const defaultValues = {
    types: [],
    priceRange: "",
    sortBy: "",
    searchQuery: "",
  };

  const methods = useForm({
    defaultValues,
  });
   const { watch, reset } = methods;
   const filters = watch();
  const filterProducts = applyFilter(productFilter, filters);
  //--------------------------------------
  const handleSubmit = (searchQuery) => {
    setPage(1);
    setFilterName(searchQuery);
  };
 const handleDispatch = (value) => {
    dispatch(handleChangeFilters(value));
  };
  
      useEffect(
    (productName) => {
      productName = filterName;

      setTimeout(() => {
        dispatch(getProducts({ page, productName }));
      }, 700);
    },
    [page, filterName, dispatch]
  );

  useEffect(() => {
    const getProduct = async () => {
      setLoading(true);
      try {
        const res = await apiService.get("/products/public");
        setProductFilter(res.data);
        setError("");
      } catch (error) {
        console.log(error);
        setError(error.message);
      }
      setLoading(false);
    };
    getProduct();
  }, []);


  console.log("products", products);
  return (
    <Container sx={{ display: "flex", minHeight: "100vh", mt: 3 }}>

        <FormProvider methods={methods}>
          <ProductFilter resetFilter={reset} />
        </FormProvider>
      <Stack sx={{ flexGrow: 1 }}>
        <Stack
          spacing={2}
          direction={{ xs: "column", sm: "row" }}
          alignItems={{ sm: "center" }}
          justifyContent="space-between"
          mb={2}
        >
          <ProductSearch handleSubmit={handleSubmit} />
          {/* <FormProvider methods={methods}> */}
            <ProductSort handleDispatch={handleDispatch} />
          {/* </FormProvider> */}
        </Stack>

        <Box sx={{ position: "relative", height: 1 }}>
          {isLoading ? (
            <LoadingScreen />
          ) : (
            <>
              {error ? (
                <Alert severity="error">{error}</Alert>
              ) : (
                <ProductList products={products} />
              )}
            </>
          )}
        </Box>

        <Box
          sx={{
            mt: { xs: 2, md: 5 },
            mb: { xs: 2, md: 5 },
            display: "flex",
            justifyContent: "center",
          }}
        >
          {totalProduct ? (
          <PaginationBar page={page} setPage={setPage} totalPage={+totalPages}/>
          ) : (
            <Typography variant="h6">No Products Yet</Typography>
          )}
        </Box>
      </Stack>
    </Container>
  );
}

export default StorePage;
