import * as React from "react";
import Box from "@mui/material/Box";
import Tab from "@mui/material/Tab";
import TabContext from "@mui/lab/TabContext";
import TabList from "@mui/lab/TabList";
import TabPanel from "@mui/lab/TabPanel";
import { Button, Grid, Typography } from "@mui/material";
import { Container } from "@mui/system";
import { newProducts, saleProducts } from "../../data/Data";
import ProductCard from "./ProductCard";
import ProductList from "./ProductList";
import { useDispatch, useSelector } from "react-redux";
import { getProductsDiscount, getProductsNew } from "./productSlice";

export default function ProductHot() {

  const { productsNew, productsDiscount } = useSelector((state) => state.product);
  const dispatch = useDispatch();

  React.useEffect(() => {
    dispatch(getProductsNew({}));
  }, [dispatch]);

   React.useEffect(() => {
    dispatch(getProductsDiscount({}));
  }, [dispatch]);
  
  const [value, setValue] = React.useState("1");

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  return (
    <Box
      sx={{
        width: "100%",
        typography: "body1",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
      }}
    >
      <Button
        sx={{
          fontSize: { xs: 18, md: 25, lg: 35 },
          m: 2,
          fontFamily: "Comic Sans MS",
          color: "#000",
        }}
      >
        Hot Products
      </Button>
      <TabContext value={value}>
        <Box
          sx={{
            borderBottom: 1,
            borderColor: "divider",
            display: "flex",

            justifyContent: "center",
          }}
        >
          <TabList onChange={handleChange} aria-label="lab API tabs example">
            <Tab
              label="Discount"
              value="1"
              sx={{
                fontSize: { xs: 14, md: 18, lg: 19 },
                  fontFamily: "Comic Sans MS",
              }}
            />
            <Tab
              label="New Product"
              value="2"
              sx={{
                fontSize: { xs: 14, md: 18, lg: 19 },
                  fontFamily: "Comic Sans MS",
              }}
            />
          </TabList>
        </Box>

        <TabPanel value="1">
         <ProductList products={saleProducts}/>
        </TabPanel>

        <TabPanel value="2">
         <ProductList products={newProducts}/>
        </TabPanel>
      </TabContext>
    </Box>
  );
}
