import { Box, styled } from '@mui/system';
import React, { useEffect, useRef, useState } from 'react'
import { ReactMarkdown } from 'react-markdown/lib/react-markdown';
import { useSelector } from 'react-redux'
import { Button } from 'react-scroll';
import rehypeRaw from 'rehype-raw';
const ButtonDescriptions = styled(Button)(({ theme }) => ({
  width: "150px",
  // backgroundColor: alpha(theme.palette.primary.main, 0.5),
}));

const DescriptionsContainer = styled(Box)(({ theme, show }) => ({
  maxHeight: show ? "auto" : "80vh",
  overflowY: "hidden",
  position: "relative",
  width: "100%",
  transition: theme.transitions.create("all", {
    easing: theme.transitions.easing.easeInOut,
    duration: theme.transitions.duration.default,
  }),
}));
function ProductDes() {
   const { currentPageProducts, productById, isLoading, totalProduct, totalPages } =
    useSelector((state) => state.product);

  let product = currentPageProducts.map((product) => productById[product]);
    const {description} = product
    const ref = useRef(0);
    console.log("product", product)
    const [show, setShow] = useState(false);
  const [showButton, setShowButton] = useState(false);

   useEffect(() => {
    if (ref.current.clientHeight < ref.current.scrollHeight) {
      setShowButton(true);
    } else {
      setShowButton(false);
    }
  }, [ref.current?.clientHeight, description?.content]);
  return (
    <DescriptionsContainer ref={ref} show={show} showButton={showButton}>
      <ReactMarkdown
        rehypePlugins={[rehypeRaw]}
        children={description?.content}
      />
      {showButton && (
        <Box
          sx={{
            zIndex: 99,
            position: show ? "relative" : "absolute",
            bottom: 0,
            display: "flex",
            justifyContent: "center",
            width: "100%",
            pt: 4,
            py: 3,
            background: "linear-gradient(to top, white 50%,transparent)",
          }}
        >
          <ButtonDescriptions
            variant="outlined"
            color="primary"
            onClick={() => setShow(show ? false : true)}
          >
            {show ? "Hide" : "Show more"}
          </ButtonDescriptions>
        </Box>
      )}
    </DescriptionsContainer>
  )
}

export default ProductDes;
