import React from "react";
import { Link, Typography } from "@mui/material";
import { Box } from "@mui/system";

function MainFooter() {
  return (
    <Box sx={{ height: 150, background: "white" }}>
      <Box
        sx={{
          mt: 2,
          mb: 2,
          display: "flex",
          flexDirection: { xs: "column", md: "row" },
          justifyContent: "space-around",
        }}
      >
        <Box p={1}>
          <Box>
            <Typography
              sx={{
                backgroundColor: "#fff",
                fontSize: { xs: 16, md: 18, lg: 25 },
                lineHeight: 2,
              }}
            >
              MiniFood
              <Typography 
              sx={{
                 fontSize: { xs: 14, md: 16, lg: 18 } }}
                 >
                üè† Address: 12 L√Ω Th∆∞·ªùng Ki·ªát, Qu·∫≠n 11, TpHCM
              </Typography>
              <Typography
                sx={{
                  fontSize: { xs: 14, md: 16, lg: 18 },
                }}
              >
                üìû Hotline: 0368634491
              </Typography>
              <Link
                sx={{
                  color: "black",
                  textDecoration: "none",
                  fontSize: { xs: 14, md: 16, lg: 18 },
                }}
              >
                üíå Email: minifood@gmail.com
              </Link>
              <Typography
                sx={{
                  fontSize: { xs: 14, md: 16, lg: 18 },
                }}
              >
                üîî Fanpage: minifood
              </Typography>
            </Typography>
          </Box>
        </Box>

        <Box p={1}>
          <Typography pb={1} sx={{ fontSize: { xs: 14, md: 16, lg: 18 } }}>
            Delivery & Payments
          </Typography>
          <Box>
            <img
              style={{ width: 90, height: "100%" }}
              alt=""
              src="./delivery/ghtk.png"
            />
            <img
              style={{ width: 90, height: "100%" }}
              alt=""
              src="./delivery/grab.png"
            />
            <img
              style={{ width: 90, height: "100%" }}
              alt=""
              src="./bank/momo.png"
            />
            <img
              style={{ width: 90, height: "100%" }}
              alt=""
              src="./bank/zalopay.png"
            />
          </Box>
        </Box>
      </Box>

      <Box>
        <Typography
          variant="body2"
          color="text.secondary"
          p={1}
          textAlign="center"
        >
          {"Copyright ¬© "}
          <Link color="inherit">Dinh Ngoc Lien Nhi</Link>{" "}
          {new Date().getFullYear()}
          {"."}
        </Typography>
      </Box>
    </Box>
  );
}

export default MainFooter;
