import * as React from "react";
import Card from "@mui/material/Card";
import CardContent from "@mui/material/CardContent";
import CardMedia from "@mui/material/CardMedia";
import Typography from "@mui/material/Typography";
import "./product.css";
import { CardActionArea, Stack } from "@mui/material";
import { useNavigate } from "react-router-dom";
import { fCurrency } from "../../utils/numberFormat";

export default function ProductCard({ product }) {
const navigate = useNavigate();
const img = product.image[0]
  return (
    <>
      <Card
      onClick={() => 
      
      navigate(`/products/public/${product.id}`)}
        className="card_product"
        sx={{
          width:"100%wh",
          borderRadius: 5,
          m: 3,
          backgroundColor: "primary.light",
          
        }}
      >
        <CardActionArea>
        <CardMedia
        className="card_media"
          sx={{
            height: {
              xs: 130,
              md: 200,
              lg: 250,
            },
            // width:"100%"
          }}
          component="img"
          image={img}
          alt=""
          key={product._id}
        />
        <CardContent
          sx={{
            height: { xs: 90 },
            mt: { xs: 2 },
            display: "flex",
            flexDirection: "column",
          }}
        >
          <Typography
            gutterBottom
            component="div"
            sx={{
              color: "primary.darker",
              display: "flex",
              justifyContent: "center",
              fontSize: { xs: 14, md: 18, lg: 20 },
              fontWeight: "700",
            }}
          >
            {product.productName}
          </Typography>
           <Stack
              direction="row"
              spacing={0.5}
              alignItems="center"
              justifyContent="flex-end"
              >
            {fCurrency(product.priceSale) && (

               <Typography
            size="medium"
            sx={{
              display: "flex",
              justifyContent: "center",
              color: "primary.darker",
              fontSize: { xs: 11, md: 18, lg: 18 },
              fontWeight: "500",
            }}
          >
           {fCurrency(product.priceSale )} 
          </Typography>

            )}

          <Typography variant="subtitle1" sx={{ fontWeight: 600, fontSize: { xs: 13 } }}>
            {fCurrency(product.price)}
            </Typography>
         
          </Stack>
        </CardContent>
        </CardActionArea>
      </Card>
    </>
  );
}
