function EnhancedTableHead(props) {
  const {
    onSelectAllClick,
    order,
    orderBy,
    numSelected,
    userCount,
    onRequestSort,
  } = props;
  const createSortHandler = (property) => (event) => {
    onRequestSort(event, property);
  };

  return (
    <TableHead>
      <TableRow>
        <TableCell padding="checkbox">
          <Checkbox
            color="primary"
            indeterminate={numSelected > 0 && numSelected < userCount}
            checked={userCount > 0 && numSelected === userCount}
            onChange={onSelectAllClick}
            inputProps={{
              "aria-label": "select all desserts",
            }}
          />
        </TableCell>
        {headCells.map((headCell) => (
          <TableCell
            key={headCell.id}
            align={headCell.numeric ? "right" : "left"}
            padding={headCell.disablePadding ? "none" : "normal"}
            sortDirection={orderBy === headCell.id ? order : false}
          >
            <TableSortLabel
              active={orderBy === headCell.id}
              direction={orderBy === headCell.id ? order : "asc"}
              onClick={createSortHandler(headCell.id)}
            >
              {headCell.label}
              {orderBy === headCell.id ? (
                <Box sx={visuallyHidden}>
                  {order === "desc" ? "sorted descending" : "sorted ascending"}
                </Box>
              ) : null}
            </TableSortLabel>
          </TableCell>
        ))}
      </TableRow>
    </TableHead>
  );
}

const EnhancedTableToolbar = (props) => {
  const dispatch = useDispatch();
  const { users } = useSelector((state) => state.user);
  const { numSelected, selected, setSelected, page, rowsPerPage } = props;
  const user = users.find((user) => user._id === selected[0] && user.isDeleted);

  const handleSubmit = (searchQuery) =>
    dispatch(handleChangeUserFilters({ name: searchQuery }));

  return (
    <Toolbar
      sx={{
        pl: { sm: 2 },
        pr: { xs: 1, sm: 1 },
        ...(numSelected > 0 && {
          bgcolor: (theme) =>
            alpha(
              theme.palette.primary.main,
              theme.palette.action.activatedOpacity
            ),
        }),
      }}
    >
      {numSelected > 0 ? (
        <Typography
          sx={{ flex: "1 1 100%" }}
          color="inherit"
          variant="subtitle1"
          component="span"
        >
          {numSelected} selected
        </Typography>
      ) : (
        <Stack direction="row" alignItems="center" sx={{ flex: "1 1 100%" }}>
          <SearchInput handleSubmit={handleSubmit} />
        </Stack>
      )}

      {numSelected > 0 && user ? (
        <Tooltip title="Active User">
          <IconButton
            onClick={() => {
              const filters = { page: page + 1, limit: rowsPerPage };
              dispatch(updateUser(selected[0], { isDeleted: false }, filters));
              setSelected([]);
            }}
          >
            <PersonIcon />
          </IconButton>
        </Tooltip>
      ) : (
        <Tooltip title="Deative User">
          <span>
            <IconButton
              disabled={numSelected === 0}
              onClick={() => {
                const filters = { page: page + 1, limit: rowsPerPage };
                dispatch(deactiveUser(selected[0], filters));
                setSelected([]);
              }}
            >
              <PersonOffIcon />
            </IconButton>
          </span>
        </Tooltip>
      )}
    </Toolbar>
  );
};
export default function UserPage() {
  const { isLoading, error, users, totalUsers, filters, totalPage } =
    useSelector((state) => state.user);
  const dispatch = useDispatch();

  const [order, setOrder] = React.useState("asc");
  const [orderBy, setOrderBy] = React.useState("name");
  const [selected, setSelected] = React.useState([]);
  const [page, setPage] = React.useState(0);

  const [rowsPerPage, setRowsPerPage] = React.useState(5);

  const handleRequestSort = (event, property) => {
    const isAsc = orderBy === property && order === "asc";
    setOrder(isAsc ? "desc" : "asc");
    setOrderBy(property);
  };

  const handleSelectAllClick = (event) => {
    if (event.target.checked) {
      const newSelecteds = users.map((n) => n._id);
      setSelected(newSelecteds);

      return;
    }
    setSelected([]);
  };

  const handleClick = (event, _id) => {
    const selectedIndex = selected.indexOf(_id);

    let newSelected = [];

    if (selectedIndex === -1) {
      newSelected = newSelected.concat(selected, _id);
    } else if (selectedIndex === 0) {
      newSelected = newSelected.concat(selected.slice(1));
    } else if (selectedIndex === selected.length - 1) {
      newSelected = newSelected.concat(selected.slice(0, -1));
    } else if (selectedIndex > 0) {
      newSelected = newSelected.concat(
        selected.slice(0, selectedIndex),
        selected.slice(selectedIndex + 1)
      );
    }

    setSelected(newSelected);
  };

  const isSelected = (title) => selected.indexOf(title) !== -1;

  // Avoid a layout jump when reaching the last page with empty users.
  const emptyRows =
    page > 0 ? Math.max(0, (1 + page) * rowsPerPage - totalUsers) : 0;

  useEffect(() => {
    const filters = { page: page + 1, limit: rowsPerPage };
    dispatch(getUserList(filters));
  }, [selected, filters, dispatch, page, rowsPerPage]);

  return (
    <Box sx={{ width: "100%" }}>
      <Stack direction="row" alignItems="center" sx={{ py: 2 }}>
        <TitleStyle>
          <GroupIcon sx={{ width: "35px", height: "35px" }} />
          <Typography variant="h6" textAlign="left" sx={{ pl: 1 }}>
            User
          </Typography>
        </TitleStyle>
      </Stack>

      {isLoading ? (
        <LoadingScreen />
      ) : (
        <>
          {error ? (
            <Alert severity="error">{error}</Alert>
          ) : (
            <>
              <Paper sx={{ width: "100%", mb: 2 }}>
                <EnhancedTableToolbar
                  numSelected={selected.length}
                  selected={selected}
                  setSelected={setSelected}
                  page={page}
                  rowsPerPage={rowsPerPage}
                />
                <TableContainer>
                  <Table
                    sx={{ minWidth: 750 }}
                    aria-labelledby="tableTitle"
                    size={"medium"}
                  >
                    <EnhancedTableHead
                      numSelected={selected.length}
                      order={order}
                      orderBy={orderBy}
                      onSelectAllClick={handleSelectAllClick}
                      onRequestSort={handleRequestSort}
                      userCount={totalUsers}
                    />
                    <TableBody>
                      {users &&
                        stableSort(users, getComparator(order, orderBy)).map(
                          (user, index) => {
                            const isItemSelected = isSelected(user._id);
                            const labelId = `enhanced-table-checkbox-${index}`;

                            return (
                              <TableRow
                                hover
                                onClick={(event) => {
                                  handleClick(event, user._id);
                                }}
                                role="checkbox"
                                aria-checked={isItemSelected}
                                tabIndex={-1}
                                key={user._id}
                                selected={isItemSelected}
                              >
                                <TableCell padding="checkbox">
                                  <Checkbox
                                    color="primary"
                                    checked={isItemSelected}
                                    inputProps={{
                                      "aria-labelledby": labelId,
                                    }}
                                  />
                                </TableCell>
                                <TableCell
                                  component="span"
                                  id={labelId}
                                  scope="user"
                                  sx={{ p: 1 }}
                                >
                                  <Avatar
                                    src={user?.avatarUrl}
                                    alt="photoURL"
                                  />
                                </TableCell>
                                <TableCell align="left">
                                  <Typography
                                    variant="subtitle2"
                                    sx={{ color: "text.primary" }}
                                  >
                                    {user?.name}
                                  </Typography>
                                </TableCell>
                                <TableCell align="right">
                                  <Typography
                                    variant="subtitle1"
                                    color="initial"
                                  >
                                    {user?.email}
                                  </Typography>
                                </TableCell>
                                <TableCell align="right">
                                  <Typography
                                    variant="subtitle1"
                                    color="initial"
                                  >
                                    {user?.phone}
                                  </Typography>
                                </TableCell>

                                <TableCell align="right">
                                  <Typography
                                    variant="subtitle1"
                                    color="initial"
                                  >
                                    <Chip
                                      label={
                                        user?.isDeleted ? "Deactive" : "Active"
                                      }
                                      color={user?.isDeleted ? "error" : "info"}
                                    />
                                  </Typography>
                                </TableCell>
                                <TableCell align="right">
                                  <Typography
                                    variant="subtitle1"
                                    color="initial"
                                  >
                                    {fDate(user?.createdAt)}
                                  </Typography>
                                </TableCell>
                                <TableCell align="right">
                                  <Stack
                                    direction="row"
                                    spacing={1}
                                    justifyContent="flex-end"
                                  >
                                    <AvatarGroup max={2}>
                                      {user?.facebookId && (
                                        <Avatar
                                          alt="Facebook"
                                          sx={{
                                            bgcolor: "success.main",
                                            width: 35,
                                            height: 35,
                                          }}
                                        >
                                          <FaFacebookF />
                                        </Avatar>
                                      )}

                                      {user?.googleId && (
                                        <Avatar
                                          src={<FaGoogle />}
                                          alt="Google"
                                          sx={{
                                            bgcolor: "success.main",
                                            width: 35,
                                            height: 35,
                                          }}
                                        />
                                      )}
                                    </AvatarGroup>
                                  </Stack>
                                </TableCell>
                              </TableRow>
                            );
                          }
                        )}
                      {emptyRows > 0 && (
                        <TableRow
                          style={{
                            height: 53 * emptyRows,
                          }}
                        >
                          <TableCell colSpan={6} />
                        </TableRow>
                      )}
                    </TableBody>
                  </Table>
                </TableContainer>
                 <Box
            sx={{
              mt: { xs: 2, md: 5 },
              mb: { xs: 2, md: 5 },
              display: "flex",
              justifyContent: "center",
            }}
          >
            {totalUsers ? (
              <PaginationBar
                page={page}
                setPage={setPage}
                totalPage={+totalPage}
              />
            ) : (
              <Typography variant="h6">No User Yet</Typography>
            )}
          </Box>
              </Paper>
            </>
          )}
         
        </>
      )}
    </Box>
  );
}
