import { Alert, Grid, Typography } from "@mui/material";
import { Box, Container, Stack } from "@mui/system";
import React, { useEffect, useState } from "react";
import { useForm } from "react-hook-form";
import apiService from "../app/apiService";
import FormProvider from "../components/form/FormProvider";
import LoadingScreen from "../components/loadingScreen/LoadingScreen";
import ProductFilter from "../features/product/ProductFilter";
import ProductList from "../features/product/ProductList";
import ProductSearch from "../features/product/ProductSearch";
import ProductSort from "../features/product/ProductSort";
import "./storepage.css";
import { getProducts } from "../features/product/productSlice";
import orderBy from "lodash/orderBy";
import { useDispatch, useSelector } from "react-redux";

function StorePage() {
  const dispatch = useDispatch();
  // const [products, setProducts ] = useState([]);
  // const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState("");
  const [filterName, setFilterName] = useState("");
  const [filterTypes, setFilterTypes] = useState("");
  const [page, setPage] = useState(1);

  const { currentPageProducts, productById, isLoading, totalProduct } =
    useSelector((state) => state.product);

  let products = currentPageProducts.map((product) => productById[product]);

  const defaultValues = {
    types: [],
    priceRange: "",
    sortBy: "featured",
    searchQuery: "",
  };

  const methods = useForm({
    defaultValues,
  });

  const { watch, reset } = methods;
  const filters = watch();

  let filterProducts = applyFilter(products, filters);
  const handleSubmit = (searchQuery) => {
    setPage(1);
    setFilterName(searchQuery);
  };
  useEffect(
    (name, types) => {
      name = filterName;
      types = filterTypes;
      setTimeout(() => {
        dispatch(getProducts({ page, name, types }));
      }, 700);
    },
    [page, filterTypes, filterName, dispatch]
  );

  console.log("products", products);
  return (
    <Container sx={{ display: "flex", minHeight: "100vh", mt: 3 }}>
      <Stack>
        <FormProvider methods={methods}>
          <ProductFilter resetFilter={reset} />
        </FormProvider>
      </Stack>
      <Stack sx={{ flexGrow: 1 }}>
        {/* <FormProvider methods={methods}> */}
          <Stack
            spacing={2}
            direction={{ xs: "column", sm: "row" }}
            alignItems={{ sm: "center" }}
            justifyContent="space-between"
            mb={2}
          >
            <ProductSearch handleSubmit={handleSubmit} />

            {/* <ProductSort /> */}
          </Stack>
        {/* </FormProvider> */}
        <Box sx={{ position: "relative", height: 1 }}>
          {isLoading ? (
            <LoadingScreen />
          ) : (
            <>
              {error ? (
                <Alert severity="error">{error}</Alert>
              ) : (
                <ProductList products={products} />
              )}
            </>
          )}
        </Box>
      </Stack>
    </Container>
  );
}

function applyFilter(filters, products) {
  const { sortBy } = filters;
  console.log("filter", filters);

  let filteredProducts = products;
  console.log("filterProducts", products);

  // SORT BY

  if (sortBy === "priceDesc") {
    filteredProducts = orderBy(products, ["price"], ["desc"]);
  }
  if (sortBy === "priceAsc") {
    filteredProducts = orderBy(products, ["price"], ["asc"]);
  }

  // FILTER PRODUCTS
  if (filters.types?.length > 0) {
    filteredProducts = products.filter((product) =>
      filters.types.includes(product.types)
    );
  }

  if (filters.priceRange) {
    filteredProducts = products.filter((product) => {
      if (filters.priceRange === "below") {
        return product.price < 4;
      }
      if (filters.priceRange === "between") {
        return product.price >= 4 && product.price <= 6;
      }
      return product.price > 6;
    });
  }
  if (filters.searchQuery) {
    filteredProducts = products.filter((product) =>
      product.name.toLowerCase().includes(filters.searchQuery.toLowerCase())
    );
  }
  return filteredProducts;
}

export default StorePage;
