import React, { useEffect, useState } from "react";
import {
    Box,
    Card,
    Container,
    Pagination,
    Stack,
    Typography,

} from "@mui/material";

import { useDispatch, useSelector } from "react-redux";
import ProductTable from "./ProductTable";
import { getProducts } from "../../product/productSlice";
import useAuth from "../../../hooks/useAuth";
import PaginationBar from "../../../components/pagination/PaginationBar";
import ProductSearch from "../features/product/ProductSearch";



function ProductDashBoard() {
    let [filterName, setFilterName] = useState("");
    let [page, setPage] = useState(1);

    const { user } = useAuth();
    const dispatch = useDispatch();
  const { currentPageProducts, productById, isLoading, totalProduct, totalPages } =
    useSelector((state) => state.product);

  let products = currentPageProducts.map((product) => productById[product]);

    useEffect((name) => {
        name = filterName
        dispatch(getProducts({ id: user._id, page, name }))
    }, [page, dispatch, user._id, filterName])


    const handleSubmit = (searchQuery) => {
        setFilterName(searchQuery);
        setPage(1)
    };
    const handleChange = (even, value) => {
        setPage(value);
    };

    return (
        <Container>
            <Typography variant="h5" sx={{ fontSize: { xs: 16, md: 28 }, fontWeight: 600, mb: 2 }}>
                My Product
            </Typography>
            <Card sx={{ p: 3 }}>
                <Stack spacing={2}>
                    <Stack direction={{ xs: "column", md: "row" }} alignItems="center">
                <ProductSearch handleSubmit={handleSubmit} />

                        <Typography
                            variant="subtitle"
                            sx={{ color: "text.secondary", ml: 1 }}
                        >
                            {totalProduct > 1
                                ? `${totalProduct} products found`
                                : totalProduct === 1
                                    ? `${totalProduct} products found`
                                    : "No products found"}
                        </Typography>

                        <Box sx={{ flexGrow: 1 }} />
                    </Stack>
                    <ProductTable products={products} />
                </Stack>
                <Box
          sx={{
            mt: { xs: 2, md: 5 },
            mb: { xs: 2, md: 5 },
            display: "flex",
            justifyContent: "center",
          }}
        >
          {totalProduct ? (
          <PaginationBar 
                page={page}
                setPage={setPage}
                totalPage={+totalPages}/>
          ) : (
            <Typography variant="h6">No Products Yet</Typography>
          )}
        </Box>
            </Card>
        </Container>
    );
}

export default ProductDashBoard;
