import { Box, Button, Card, Container, Typography } from '@mui/material';
import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux';
import { Link, useNavigate } from 'react-router-dom'
import FormProvider from '../../components/form/FormProvider'
import useAuth from '../../hooks/useAuth';
import "./cart.css";
import { addCart, deleteCart, updateCart } from './cartSlice';


function Cart({cartCount, setCartCount }) {
  const { user } = useAuth();
  const {carts, page} = useSelector((state) => state.cart);

  const dispatch = useDispatch();
  const navigate = useNavigate();
  const [amountInput, setAmountInput] = useState(false)

  const handleInc = (id, amount) => {
    setAmountInput(false);
    setCartCount(
      cartCount.map((item)=> 
      id === item._id
      
      ? { ...item, amount: item.amount + 1}
      : item
      
      )
  
    );
    console.log("id",id)
    amount += 1;
    dispatch(updateCart(id, amount));
  };

  const handleDec = (id, amount) => {
    setAmountInput(false);
    setCartCount(
      cartCount.map((item)=>
      id === item._id
      ? { ...item, amount : item.amount - (item.amount > 1 ? 1 : 0)}
      :item
      )
    );
    amount -= (amount > 1 ? 1 : 0);
    dispatch(updateCart(id, amount));
  };

  const handleDel = (id) => {
    if(user) {
      dispatch(deleteCart(id));
    };
  }
  return (
    <Container className="cart-container">
      <Typography variant='h4' textAlign="center">Shopping Cart</Typography>
    
        <Box className="cart-empty">
          <Typography variant="h5" textAlign="center">Your cart is currently empty</Typography>
          <Box className="start-shopping">
            <Link to="/">
              <Typography variant="h6"> ðŸ”™ Start Shopping</Typography>
            </Link>
          </Box>
        </Box>
      
        <Box>
          <Box className="titles">
            <h3 className="product-title">Product</h3>
            <h3 className="price">Price</h3>
            <h3 className="quantity">Quantity</h3>
            <h3 className="total">Total</h3>
          </Box>
          <Box className="cart-items">
              {cartCount &&
              cartCount.map((cart)=> (
               
               
                <Box className="cart-item" key={cart.productId._id}>
                  <Box className="cart-product">
                   <img src={cart.productId.image[0]} alt="" />
                    <div>
                     <h3>{cart.productId.productName}</h3>
                      <p></p>
                      <Button
                     onClick={(e)=> {
                      handleDel(cart.productId._id)
                    }}
                      >
                        Remove
                      </Button>
                    </div>
                  </Box>
                  <div className="cart-product-price">$ {cart.productId.price}</div>
                  <div className="cart-product-quantity">
                    <Button
                   onClick={()=> {
                    handleDec(cart.productId._id, cart.productId.amount)
                   }}
                    >
                      âšŠ
                    </Button>
                    <div className="count"></div>
                    <Button
                   onClick={()=> {
                    handleInc(cart.productId._id, cart.productId.amount)
                   }}
                    >
                      âœš
                    </Button>
                  </div>
                  <div className="cart-product-total-price">
                     $
                  </div>
                </Box>
              ))}
          </Box>
          <Box className="cart-summary">
            <Button 
            className="clear-btn"
            
            >
              Clear Cart
            </Button>
            <Box className="cart-checkout">
              <Box className="subtotal">
                <span>Subtotal</span>
                <span className="amount">$</span>
              </Box>
              <Typography>Taxes and shipping calculated at checkout</Typography>
              <Button>Check out</Button>
              <Box className="continue-shopping">
                <Link to="/store">
                 
                  <Typography variant="h6">ðŸ”™ Continue Shopping</Typography>
                </Link>
              </Box>
            </Box>
          </Box>
        </Box>
      
    </Container>
  )
}

export default Cart;
