import { Box, Container, Grid } from "@mui/material";
import { addDays } from "date-fns";
import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getReportsDashboard } from "../../features/dashboard/dashboardSlice";
import { getArrayLastDays } from "../../utils/formatTime";
import AppUserChart from "./components/AppUserChart";
import AppWidgetSummary from "./components/AppWidgetSummary";



export default function DashboardPage() {
  const dispatch = useDispatch();
const { reports } = useSelector((state) => state.dashboard);
console.log("reports", reports)
const rangeDays = getArrayLastDays(7, false, addDays(new Date(), 1)).join(
    ","
  );

  useEffect(() => {
    const filters = { rangeDays };
    const intervalId = setInterval(() => {
      dispatch(getReportsDashboard(filters));
    }, 3000);
     return () => clearInterval(intervalId);
  }, [dispatch]);

 useEffect(() => {
    const filters = { rangeDays };
    dispatch(getReportsDashboard(filters));
  }, []);


return (
    <Box>
      <Container maxWidth={false}>
        <Grid container spacing={3}>
          <Grid item lg={3} sm={6} xl={3} xs={12}>
            <AppWidgetSummary title="Weekyly Sales" total={1000000} color="primary" icon={'ant-design:android-filled'} />
          </Grid>
          <Grid item xl={3} lg={3} sm={6} xs={12}>
            <AppWidgetSummary title="New Users" total={1352831} color="info" icon={'ant-design:apple-filled'} />
          </Grid>
          <Grid item xl={3} lg={3} sm={6} xs={12}>
            <AppWidgetSummary title="Item Orders" total={1723315} color="warning" icon={'ant-design:windows-filled'} />          </Grid>
          <Grid item xl={3} lg={3} sm={6} xs={12}>
            <AppWidgetSummary title="Total Product" total={234} color="secondary" icon={''} />

          </Grid>
          <Grid item xl={12} lg={12} md={12} xs={12}>
            {/* <Order
              lastestOrders={reports.lastestOrders}
              rangeDays={rangeDays}
            /> */}
            {/* <AppUserChart/> */}
          </Grid>
          <Grid item xl={4} lg={4} md={12} xs={12}>
            {/* <LatestProducts sx={{ height: "100%" }} /> */}
          </Grid>
          <Grid item xl={8} lg={8} md={12} xs={12}>
            {/* <LatestOrders /> */}
          </Grid>
        </Grid>
      </Container>
    </Box>
  );
}
