import { createSlice } from "@reduxjs/toolkit";
import { toast } from "react-toastify";


const initialState ={
    isLoading: false,
    error: null,
    productId: {},
    carts: [],
    page:1,

};

const slice = createSlice({
    name: "cart",
    initialState,
    reducers: {
        startLoading(state) {
            state.isLoading = true;
        },
        hasError(state, action) {
            state
        }
    }
})
