import { Box, Container, Grid } from "@mui/material";
import { addDays } from "date-fns";
import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getReportsDashboard } from "../../features/dashboard/dashboardSlice";
import LastOrders from "../../features/dashboard/reportStore/LastOrder";
import { getProductList } from "../../features/product/productSlice";
import { getArrayLastDays } from "../../utils/formatTime";
import AppUserChart from "./components/AppUserChart";
import AppWidgetSummary from "./components/AppWidgetSummary";
import StorefrontIcon from '@mui/icons-material/Storefront';
import { getOrder, getOrdersDashboard } from "../../features/order/orderSlice";
import { getUserList } from "../../features/user/userSlice";
import LastProduct from "../../features/dashboard/reportStore/LastProduct";
import Order from "../../features/dashboard/reportStore/Order";


export default function DashboardPage() {
  const dispatch = useDispatch();
const {reports} = useSelector((state) => state.dashboard);
const totalUser = reports.totalCustomer
const totalOrder = reports.totalOrder
const totalProduct = reports.totalProduct
const revenue = reports.revenue

console.log("totalUser", totalUser);

const rangeDays = getArrayLastDays(7, false, addDays(new Date(), 1)).join(
    ","
  );

  useEffect(() => {
    const filters = { rangeDays };
      dispatch(getReportsDashboard(filters));
  }, [dispatch]);

//  useEffect(() => {
//     const filters = { rangeDays };
//     dispatch(getReportsDashboard(filters));
//   }, [dispatch]);

  // 
  const [page, setPage] = useState(1);

  const [rowsPerPage, setRowsPerPage] =useState(10);

  const { products, totalProductDashboard, filters } = useSelector((state) => state.product);

  const {totalOrderDashboard, ordersDashboard} = useSelector((state)=> state.order);
  const {totalUsers} = useSelector((state)=> state.user);

  useEffect(() => {
    const filters = { page:page,limit:rowsPerPage  };
    dispatch(getProductList(filters));
  }, [dispatch, page, rowsPerPage,filters,]);

  
 useEffect(() => {
    const filter = { sortBy: "createdAt.desc" };
    dispatch(getOrdersDashboard(filter));
  }, [dispatch, page]);

 useEffect(()=> {
  dispatch(getUserList({page}));
 }, [dispatch, page])

return (
    <Box>
      <Container maxWidth={false}>
        <Grid container spacing={3}>
          <Grid item lg={3} sm={6} xl={3} xs={12}>
            {revenue.map((sale)=> (
            <AppWidgetSummary title="Total Price Sales" total={sale.total} color="primary" icon={'twemoji:dollar-banknote'} />
            ))}
          </Grid>
          <Grid item xl={3} lg={3} sm={6} xs={12}>
            {totalUser.map((user)=> (
            <AppWidgetSummary title="New Users" total={user.count} color="info" icon={'mdi:account-box'} />
            ))}
          </Grid>
          <Grid item xl={3} lg={3} sm={6} xs={12}>
            {totalOrder.map((order)=> (
            <AppWidgetSummary title="Total Orders" total={order.count} color="warning" icon={'typcn:device-desktop'} />         
            ))}
             </Grid>
          <Grid item xl={3} lg={3} sm={6} xs={12}>
            {totalProduct.map((product)=> (
            <AppWidgetSummary title="Total Product" total={product.count} color="addins" icon={'bi:cart4'} />
            ))}

          </Grid>
          <Grid item xl={12} lg={12} md={12} xs={12}>
            <Order
              lastestOrders={reports.lastestOrders}
              rangeDays={rangeDays}
            />
            {/* <AppUserChart/> */}
          </Grid>
          <Grid item xl={4} lg={4} md={12} xs={12}>
            <LastProduct/>
          </Grid>
          <Grid item xl={8} lg={8} md={12} xs={12}>
            <LastOrders/>
          </Grid>
        </Grid>
      </Container>
    </Box>
  );
}
