import { Box, Card, Divider, Grid, IconButton, Typography } from '@mui/material';
import React from 'react'
import { fCurrency } from '../../../utils/numberFormat';
import ButtonStatus from './ButtonStatus';
import MoreHorizIcon from "@mui/icons-material/MoreHoriz";


function OrderListDas({ordersDashboard, handleClickOpen}) {
  return (
    <Grid container spacing={2} mt={1} mb={1}>
            {ordersDashboard?.map((order) => (
              <Grid key={order?._id} item xs={12} md={4} lg={3}>
                <Card sx={{ p: 3, mt: 1, mb: 2, mr: 2, minHeight: 500 }}>
                  <Box
                    sx={{
                      mb: 2,
                      display: "flex",
                      justifyContent: "space-between",
                    }}
                  >
                    <ButtonStatus status={order?.status} />
                    <IconButton
                      onClick={() => {
                        handleClickOpen(order);
                      }}
                      sx={{ height: 30, width: 30 }}
                    >
                      <MoreHorizIcon />
                    </IconButton>
                  </Box>
                  <Typography sx={{ mb: 2, fontSize: 13, fontWeight: 600 }}>
                    CodeID: {order?.orderId}
                  </Typography>

                  <Typography sx={{ mb: 2, fontSize: 13, fontWeight: 600 }}>
                    Buyer Name: {order?.buyerName}
                  </Typography>

                  <Typography sx={{ mb: 2, fontSize: 13, fontWeight: 600 }}>
                    Address Shiping: {order?.addressShiping}
                  </Typography>

                  <Typography sx={{ mb: 2, fontSize: 13, fontWeight: 600 }}>
                    Phone: {order?.phone}
                  </Typography>

                  <Divider sx={{ borderStyle: "dashed", mb: 2 }} />

                  <Typography sx={{ fontSize: 13, mb: 2, fontWeight: 600 }}>
                    Product: {order?.nameProduct}
                  </Typography>

                  <Typography
                    sx={{
                      fontSize: 13,
                      mb: 1,
                      fontWeight: 600,
                      display: "flex",
                      justifyContent: "space-between",
                      flexDirection: "row",
                    }}
                  >
                    <span>Price:</span>
                    <span style={{ color: "red" }}>
                      {fCurrency(order.price)}{" "}
                    </span>
                  </Typography>

                  <Typography
                    sx={{
                      fontSize: 13,
                      mb: 1,
                      fontWeight: 600,
                      display: "flex",
                      justifyContent: "space-between",
                      flexDirection: "row",
                    }}
                  >
                    <span>Amount:</span>
                    <span style={{ color: "red" }}>{order?.amount}</span>
                  </Typography>

                  <Typography
                    sx={{
                      fontSize: 13,
                      mb: 1,
                      fontWeight: 600,
                      display: "flex",
                      justifyContent: "space-between",
                      flexDirection: "row",
                    }}
                  >
                    <span>Sum:</span>
                    <span style={{ color: "red" }}>
                      {fCurrency(order?.sum)}{" "}
                    </span>
                  </Typography>

                  <Divider sx={{ borderStyle: "dashed", mb: 2 }} />

                  <Typography
                    sx={{ fontStyle: "italic", fontSize: 12, fontWeight: 300 }}
                  >
                    (Pay the money when receiving the goods)
                  </Typography>
                </Card>
              </Grid>
            ))}
          </Grid>
  )
}

export default OrderListDas
